    private void listPeopleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_listPeopleActionPerformed
        // TODO add your handling code here:
       Department dep = (Department)depList.getFromIndex(StartFrame.index);
       if(dep.peopleList.isEmpty())
       {    
          new ListNoPeople().setVisible(true);
          this.setVisible(false);
       }
       else
       {
          ListPeople lp = new ListPeople();
          lp.setVisible(true);
          this.setVisible(false);
       }
    }//GEN-LAST:event_listPeopleActionPerformed

