    private void deletePersonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deletePersonActionPerformed
        // TODO add your handling code here:
       Department dep = (Department)depList.getFromIndex(StartFrame.index);
       if(dep.peopleList.isEmpty())
       {
          new UnableToDelete().setVisible(true);
          this.setVisible(false);
       }    
       else
       {
          new DeletePerson().setVisible(true);
          this.setVisible(false);
       }    
    }//GEN-LAST:event_deletePersonActionPerformed

